mapper.insert:
INSERT INTO tbl_excelfiledata
(excelfiledata_name, excelfiledata_type, excelfiledata_upload_time, excelfiledata_processing_time, excelfiledata_operation_status, excelfiledata_result)
VALUES(#{excelfiledataName}, #{excelfiledataType}, #{excelfiledataUploadTime}, #{excelfiledataProcessingTime}, #{excelfiledataOperationStatus}, #{excelfiledataResult});

mapper.select:
SELECT excelfile_name
FROM tbl_excelfile
WHERE excelfile_phone_num = #{excelfilePhoneNum}
