mapper.insert:
INSERT INTO tbl_excelfiledata
(file_name, file_type, file_created_at, file_processed_at, operation_status, consequence)
VALUES(#{fileName}, #{fileType}, #{fileCreatedAt}, #{fileProcessedAt}, #{operationStatus}, #{consequence});

mapper.select:
SELECT name
FROM tbl_excelfile
WHERE phone_num = #{phoneNum}
