mapper.insert:
INSERT INTO tbl_excelfiledata
(file_name, file_type, file_created_at, file_processed_at, operation_status, consequence)
VALUES(#{fileName}, #{fileType}, #{fileCreatedAt}, #{fileProcessedAt}, #{operationStatus}, #{consequence});

mapper.select:
SELECT name
FROM tbl_excelfile
WHERE phone_num = #{phoneNum}

mapper.update:
UPDATE tbl_excelfiledata
SET temp_file_name = #{tempFileName}, temp_file_name_origin = #{tempFileNameOrigin}
WHERE file_name = #{tempFileNameOrigin} AND temp_file_name_origin IS NULL

UPDATE tbl_excelfiledata
SET operation_status = #{operationStatus}, file_processed_at = #{fileProcessedAt}, consequence = #{consequence}
WHERE temp_file_name = #{tempFileName}
